#! /usr/bin/env node

const newNode = require('../dist/new-node');
const nomnom = require('nomnom');

const options = nomnom
	.script('axway-flow')
	.options({
		name: {
			abbr: 'n',
			help: 'The name of the flow-node, e.g. "base64".'
		},
		description: {
			abbr: 'd',
			help: 'Description of the flow-node handler (optional).'
		},
		help: {
			abbr: 'h',
			help: 'This usage.',
			flag: true
		}
	});

const opts = options.parse();

if (!opts.name) {
	options.parse(['-h']);
	process.exit(-1);
}

newNode(opts);
